sys_script_include.script:
var CompareAJAX = Class.create();
CompareAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// Called from UI Action: Compare
	compareAction: function () {
		var oReturn = {};
		var _ct = new x_snc_compare_tool.CompareTool(this.getParameter('sysparm_sys_id'), this.getParameter('sysparm_number'), this.getParameter('sysparm_type'), this.getParameter('sysparm_output_format'), this.getParameter('sysparm_output_filter'), this.getParameter('sysparm_record_left'), this.getParameter('sysparm_record_right'), this.getParameter('sysparm_key_type'), this.getParameter('sysparm_key_fields'), this.getParameter('sysparm_output_fields'), this.getParameter('sysparm_table_left'), this.getParameter('sysparm_table_right'), this.getParameter('sysparm_connection_alias_left'), this.getParameter('sysparm_connection_alias_right'), this.getParameter('sysparm_filter_condition'), this.getParameter('sysparm_status'), oReturn);
		_ct.compareAction();
		return JSON.stringify(oReturn);
	}, // function compareAction

	type: 'CompareAJAX',
	version: '1.0.0',
	author: 'Tom Hauri',
	email: 'tom@hauri.biz'
});
