sys_script_include.script:
var CompareAJAX = Class.create();
CompareAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// Called from UI Action: Compare
	start: function () {
		try {
			var gsHierarchicalWorker = new GlideScriptedHierarchicalWorker();
			gsHierarchicalWorker.setProgressName('Compare_' + this.getParameter('sysparm_ajax_processor_number'));
			gsHierarchicalWorker.setScriptIncludeName('x_snc_compare_tool.CompareTool');
			gsHierarchicalWorker.setScriptIncludeMethod('compareAction');
			var aVariables = ['sCompareId', 'sNumber', 'sType', 'sOutputFormat', 'sOutputFilter', 'sRecordLeftId', 'sRecordRightId', 'sKeyType', 'sKeyFields', 'sOutputFields', 'sTableLeft', 'sTableRight', 'sConnectionAliasLeftId', 'sConnectionAliasRightId', 'sFilterCondition', 'sStatus', 'oReturn'];
			['sys_id', 'number', 'type', 'output_format', 'output_filter', 'record_left', 'record_right', 'key_type', 'key_fields', 'output_fields', 'table_left', 'table_right', 'connection_alias_left', 'connection_alias_right', 'filter_condition', 'status'].forEach(function (sArgument, i) {
				gsHierarchicalWorker.putConstructorArg(aVariables[i], this.getParameter('sysparm_ajax_processor_' + sArgument));
			}, this); // each argument
			gsHierarchicalWorker.setBackground(true);
			gsHierarchicalWorker.start();
			gs.debug('x_snc_compare_tool.CompareAJAX.start worker: ' + gsHierarchicalWorker.getProgressID());
		} catch (oEx) {
			gs.error('x_snc_compare_tool.CompareAJAX.start oEx: ' + String(oEx));
		} // try
	}, // function start

	checkRunning: function () {
		var sNumber = this.getParameter('sysparm_number');
		if (sNumber) {
			var grProgressWorker = new GlideRecord('sys_progress_worker');
			grProgressWorker.setLimit(1);
			grProgressWorker.addEncodedQuery('name=Compare_' + sNumber + '^state=running');
			grProgressWorker.query();
			if (!grProgressWorker.hasNext()) {
				return 'false';
			} // if no running worker
		} // if number
		return 'true';
	}, // function checkRunning

	type: 'CompareAJAX',
	version: '1.0.0',
	author: 'Tom Hauri',
	email: 'tom@hauri.biz'
});
